    private int bans = 0;

    // Ranks
    private enum Rank {
        MOD(60 * 10),
        ADMIN(60 * 20),
        OWNER(Integer.MAX_VALUE);

        private final int weeklyGoal;

        Rank(int weeklyGoal) {
            this.weeklyGoal = weeklyGoal;
        }

        public int getWeeklyGoal() {
            return weeklyGoal;
        }
    }

    // Get player rank
    private Rank getRank(Player player) {
        Rank rank = playerRanks.get(player);
        if (rank == null) {
            rank = Rank.MOD;
            playerRanks.put(player, rank);
        }
        return rank;
    }

    // Playtime tracking
    @EventHandler
    public void onPlayerJoin(PlayerJoinEvent event) {
        Player player = event.getPlayer();
        long joinTime = System.currentTimeMillis();
        sessionJoinTime.put(player, joinTime);
        updatePlaytime(player);
    }

    @EventHandler
    public void onPlayerQuit(PlayerQuitEvent event) {
        Player player = event.getPlayer();
        Long joinTime = sessionJoinTime.get(player);

        if (joinTime != null) {
            long timePlayed = System.currentTimeMillis() - joinTime;
            totalPlaytime.merge(player, timePlayed, Long::sum);
        }
        sessionJoinTime.remove(player);
    }

    private void updatePlaytime(Player player) {
        Rank rank = getRank(player);
        int weeklyGoal = rank.getWeeklyGoal();

        int minutesPlayed = playtimeMinutes.getOrDefault(player, 0);
        int minutesSession = getSessionPlaytime(player);

        minutesPlayed += minutesSession;
        playtimeMinutes.put(player, minutesPlayed);

        int minutesRemaining = weeklyGoal - minutesPlayed;
        int hoursRemaining = minutesRemaining / 60;

        player.sendMessage(&quot;You have &quot; + hoursRemaining + &quot; hours remaining!&quot;);
    }

    private int getSessionPlaytime(Player player) {
        long joinTime = sessionJoinTime.get(player);
        long now = System.currentTimeMillis();
        int minutes = (int) ((now - joinTime) / 1000) / 60;
        return minutes;
    }

