package nl.officialfox.staffmanage;

import net.luckperms.api.LuckPerms;
import net.luckperms.api.model.user.User;
import net.luckperms.api.query.QueryOptions;
import org.bukkit.Bukkit;
import org.bukkit.ChatColor;
import org.bukkit.Material;
import org.bukkit.command.Command;
import org.bukkit.command.CommandExecutor;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.player.PlayerJoinEvent;
import org.bukkit.event.player.PlayerQuitEvent;
import org.bukkit.inventory.Inventory;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.meta.ItemMeta;
import org.bukkit.plugin.java.JavaPlugin;
import org.jetbrains.annotations.NotNull;

import java.text.NumberFormat;
import java.text.ParseException;
import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.concurrent.TimeUnit;

public class Staffmanage extends JavaPlugin implements Listener, CommandExecutor {

    // Playtime tracking
    private HashMap&lt;Player, Integer&gt; playtimeMinutes = new HashMap&lt;&gt;();
    private LuckPerms luckPerms = (LuckPerms) Bukkit.getServer().getPluginManager().getPlugin(&quot;LuckPerm&quot;);
    private Map&lt;Player, Long&gt; totalPlaytime = new HashMap&lt;&gt;();
    private HashMap&lt;Player, Rank&gt; playerRanks = new HashMap&lt;&gt;();
    private HashMap&lt;Player, Long&gt; sessionJoinTime = new HashMap&lt;&gt;();

    private int warnings = 0;
    //    LuckPerms luckPerms = (LuckPerms) Bukkit.getServer().getPluginManager().getPlugin(&quot;LuckPerms&quot;);
    private int bans = 0;

    // Ranks
    private enum Rank {
        MOD(60 * 10),
        ADMIN(60 * 20),
        OWNER(Integer.MAX_VALUE);

        private final int weeklyGoal;

        Rank(int weeklyGoal) {
            this.weeklyGoal = weeklyGoal;
        }

        public int getWeeklyGoal() {
            return weeklyGoal;
        }
    }

    // Get player rank
    private Rank getRank(Player player) {
        Rank rank = playerRanks.get(player);
        if (rank == null) {
            rank = Rank.MOD;
            playerRanks.put(player, rank);
        }
        return rank;
    }

    // Playtime tracking
    @EventHandler
    public void onPlayerJoin(PlayerJoinEvent event) {
        Player player = event.getPlayer();
        long joinTime = System.currentTimeMillis();
        sessionJoinTime.put(player, joinTime);
        updatePlaytime(player);
    }

    @EventHandler
    public void onPlayerQuit(PlayerQuitEvent event) {
        Player player = event.getPlayer();
        Long joinTime = sessionJoinTime.get(player);

        if (joinTime != null) {
            long timePlayed = System.currentTimeMillis() - joinTime;
            totalPlaytime.merge(player, timePlayed, Long::sum);
        }
        sessionJoinTime.remove(player);
    }

    private void updatePlaytime(Player player) {
        Rank rank = getRank(player);
        int weeklyGoal = rank.getWeeklyGoal();

        int minutesPlayed = playtimeMinutes.getOrDefault(player, 0);
        int minutesSession = getSessionPlaytime(player);

        minutesPlayed += minutesSession;
        playtimeMinutes.put(player, minutesPlayed);

        int minutesRemaining = weeklyGoal - minutesPlayed;
        int hoursRemaining = minutesRemaining / 60;

        player.sendMessage(&quot;You have &quot; + hoursRemaining + &quot; hours remaining!&quot;);
    }

    private int getSessionPlaytime(Player player) {
        long joinTime = sessionJoinTime.get(player);
        long now = System.currentTimeMillis();
        int minutes = (int) ((now - joinTime) / 1000) / 60;
        return minutes;
    }

    // Command
    @Override
    public boolean onCommand(CommandSender sender, Command cmd, String label, String[] args) {
        if (!(sender instanceof Player)) {
            sender.sendMessage(&quot;This command can only be used by players!&quot;);
            return true;
        }

        Player player = (Player) sender;

        if (label.equals(&quot;gocraftstaff&quot;)) {
            try {
                openStaffUI(player);
            } catch (ParseException e) {
                throw new RuntimeException(e);
            }

            return true;
        }
        return false;
    }

    private void openStaffUI(Player player) throws ParseException {

        Inventory gui = Bukkit.createInventory(player, 27, ChatColor.DARK_GREEN + &quot;Staff UI&quot;);

        gui.setItem(11, createPlaytimeItem(player));
        gui.setItem(13, createOnlineStaffItem(getOnlineStaff()));
        gui.setItem(15, createStatsItem());

        player.openInventory(gui);

    }

    private ItemStack createPlaytimeItem(Player player) throws ParseException {

        // Gebruik getOrDefault om default waarde te retourneren als null
        long originalPlaytime = totalPlaytime.getOrDefault(player, 0L);

        ItemStack item = new ItemStack(Material.CLOCK);
        ItemMeta meta = item.getItemMeta();

        meta.setDisplayName(ChatColor.GOLD + &quot;Jouw online tijd&quot;);

        List&lt;String&gt; lore = new ArrayList&lt;&gt;();

        String playtimeString = getPlaytime(player);

        lore.add(ChatColor.GRAY + playtimeString);

        // Controleer of originalPlaytime 0 is
        if(originalPlaytime != 0) {

            String formatted = TimeUnit.MILLISECONDS.toDays(originalPlaytime) + &quot;d &quot; +
                    TimeUnit.MILLISECONDS.toHours(originalPlaytime) % 24 + &quot;u &quot; +
                    TimeUnit.MILLISECONDS.toMinutes(originalPlaytime) % 60 + &quot;m&quot;;

            lore.add(ChatColor.GRAY + formatted);

        }

        meta.setLore(lore);
        item.setItemMeta(meta);

        return item;

    }



    private String getPlaytime(Player player) throws ParseException {

        long playtime = totalPlaytime.getOrDefault(player, 0L);
        if(playtime == 0) {
            return &quot;0&quot;;
        } else {

            NumberFormat format = NumberFormat.getInstance();
            String playtimeString = format.parse(TimeUnit.MILLISECONDS.toDays(playtime) + &quot;d &quot; +
                    TimeUnit.MILLISECONDS.toHours(playtime) % 24 + &quot;u &quot; +
                    TimeUnit.MILLISECONDS.toMinutes(playtime) % 60 + &quot;m&quot;).toString();


            return playtimeString;
        }

    }

    private List&lt;Player&gt; getOnlineStaff() {

        List&lt;Player&gt; onlineStaff = new ArrayList&lt;&gt;();

        for (Player player : Bukkit.getOnlinePlayers()) {
            if (isStaff(player)) {
                onlineStaff.add(player);
            }
        }

        return onlineStaff;

    }

    // Check if player is staff
    private boolean isStaff(Player player) {
        // Check for staff permission
        if (player.hasPermission(&quot;staff.member&quot;)) {
            return true;
        }
        // Check if player is in staff group
        else if (isInGroup(player, &quot;Staff&quot;)) {
            return true;
        } else {
            System.out.println(&quot;U bent geen STAFF&quot;);
            return false;
        }
    }

    private boolean isInGroup(Player player, String group) {
        QueryOptions options = QueryOptions.defaultContextualOptions();
        User user = luckPerms.getUserManager().getUser(player.getUniqueId());
        return user.getInheritedGroups(options).contains(group);
    }

    public void warnPlayer(Player player) {
        warnings++;
    }

    // Doe ban
    public void banPlayer(Player player) {
        bans++;
    }

    // Create online staff item
    private ItemStack createOnlineStaffItem(List&lt;Player&gt; onlineStaff) {

        ItemStack item = new ItemStack(Material.PLAYER_HEAD);
        ItemMeta meta = item.getItemMeta();

        meta.setDisplayName(ChatColor.AQUA + &quot;Online Staff&quot;);

        List&lt;String&gt; lore = new ArrayList&lt;&gt;();

        for (Player staff : onlineStaff) {
            lore.add(ChatColor.GRAY + &quot;- &quot; + staff.getName());
        }

        meta.setLore(lore);
        item.setItemMeta(meta);

        return item;

    }

    // Create stats item
    private ItemStack createStatsItem() {

        ItemStack item = new ItemStack(Material.PAPER);
        ItemMeta meta = item.getItemMeta();

        meta.setDisplayName(ChatColor.GOLD + &quot;Staff Stats&quot;);

        List&lt;String&gt; lore = new ArrayList&lt;&gt;();
        lore.add(ChatColor.GRAY + &quot;Warnings this week: &quot; + warnings);
        lore.add(ChatColor.GRAY + &quot;Bans this week: &quot; + bans);

        meta.setLore(lore);
        item.setItemMeta(meta);

        return item;

    }

    @Override
    public void onEnable() {
        getServer().getPluginManager().registerEvents(this, this);
        getCommand(&quot;gocraftstaff&quot;).setExecutor(this);
    }
}

Is er misschien een makkelijker manier om de tijd op te slaan en te gebruiken?

 Hier een voorbeeld hoe je het zou kunnen implementeren met Java Time:

```java
import java.time.Duration;
import java.time.Instant;
import java.util.Map;
import java.util.HashMap;

public class PlaytimeTracker {

  private Map<Player, Instant> joinTimes = new HashMap<>();

  public void playerJoined(Player player) {
    joinTimes.put(player, Instant.now()); 
  }

  public String getPlaytime(Player player) {
    Instant joinTime = joinTimes.get(player);
    if (joinTime == null) return "0m";

    Duration playtime = Duration.between(joinTime, Instant.now());

    long minutes = playtime.toMinutes();
    return String.format("%dm", minutes); 
  }

  public void playerLeft(Player player) {
    joinTimes.remove(player);
  }

}
```

De truc is dus:

- Sla alleen de join tijd op, niet de totale tijd
- Gebruik Duration.between om het verschil te berekenen
- Formatteren naar minuten met .toMinutes()

Op deze manier blijft de tijd bijhouden een stuk eenvoudiger.

private ItemStack createPlaytimeItem(Player player) throws ParseException {

        // Gebruik getOrDefault om default waarde te retourneren als null
        long originalPlaytime = totalPlaytime.getOrDefault(player, 0L);

        ItemStack item = new ItemStack(Material.CLOCK);
        ItemMeta meta = item.getItemMeta();

        meta.setDisplayName(ChatColor.GOLD + &quot;Jouw online tijd&quot;);

        List&lt;String&gt; lore = new ArrayList&lt;&gt;();

        String playtimeString = getPlaytime(player);

        lore.add(ChatColor.GRAY + playtimeString);

        // Controleer of originalPlaytime 0 is
        if(originalPlaytime != 0) {

            String formatted = TimeUnit.MILLISECONDS.toDays(originalPlaytime) + &quot;d &quot; +
                    TimeUnit.MILLISECONDS.toHours(originalPlaytime) % 24 + &quot;u &quot; +
                    TimeUnit.MILLISECONDS.toMinutes(originalPlaytime) % 60 + &quot;m&quot;;

            lore.add(ChatColor.GRAY + formatted);

        }

        meta.setLore(lore);
        item.setItemMeta(meta);

        return item;

    }

Hoe pas ik die class toe in hier?

 Hier is een voorbeeld hoe je de PlaytimeTracker class kunt gebruiken in de createPlaytimeItem methode:

```java
private PlaytimeTracker playtimeTracker = new PlaytimeTracker();

private ItemStack createPlaytimeItem(Player player) {

  ItemStack item = new ItemStack(Material.CLOCK);
  ItemMeta meta = item.getItemMeta();

  meta.setDisplayName(ChatColor.GOLD + "Jouw online tijd");

  List<String> lore = new ArrayList<>();

  // Haal de playtime op
  String playtime = playtimeTracker.getPlaytime(player);

  // Voeg toe aan lore
  lore.add(ChatColor.GRAY + playtime);

  // Check voor originele playtime
  if(originalPlaytime > 0) {
    // ...originele playtime formatting
  }

  meta.setLore(lore);
  item.setItemMeta(meta);

  return item;

}

// Roep aan bij events
@EventHandler
public void onJoin(PlayerJoinEvent event) {

  Player player = event.getPlayer();

  playtimeTracker.playerJoined(player);

}

@EventHandler 
public void onQuit(PlayerQuitEvent event) {

  Player player = event.getPlayer();

  playtimeTracker.playerLeft(player);

}
```

De key punten:

- Maak een PlaytimeTracker instantie 
- Roep de juiste methods aan bij events
- Gebruik `getPlaytime()` om de huidige playtime op te halen
- Rest van de code blijft grotendeels hetzelfde

Op deze manier hou je de tijd bij in de PlaytimeTracker class en gebruik je het alleen om de playtime weer te geven in de UI.

 Je krijgt de "cannot resolve symbol" fout voor originalPlaytime omdat die variabele niet gedefinieerd is in de createPlaytimeItem methode. 

De originalPlaytime variabele wordt gebruikt om de totale speeltijd op te slaan, maar in het nieuwe voorbeeld slaan we alleen de jointime op in de PlaytimeTracker class.

Je kunt originalPlaytime verwijderen uit de createPlaytimeItem methode, je hebt het niet nodig daar.

De check voor originalPlaytime > 0 kan je ook weghalen. De PlaytimeTracker class retourneert altijd een playtime, dus je hoeft niet te checken op 0.

Het zou er ongeveer zo uit kunnen zien:

```java
private ItemStack createPlaytimeItem(Player player) {

  // ...

  String playtime = playtimeTracker.getPlaytime(player);

  lore.add(ChatColor.GRAY + playtime);

  // Verwijder check voor originalPlaytime 

  // ...

}
```

Samenvattend:

- Verwijder de originalPlaytime variabele
- Verwijder de check op originalPlaytime 
- Gebruik alleen de playtime van de PlaytimeTracker

Op die manier zou het probleem met de "cannot resolve symbol" fout opgelost moeten zijn. Laat me weten als je nog andere vragen hebt!

